function extractTextAndDate(input){
  const regex = /(gia vang|ty gia|bitcoin|tong hop)(?: ngay)?(?: (\d{2}\/\d{2}\/\d{4}))?/i;
  const match = input.match(regex);
  function isValidDate(dateStr) {
        if (!dateStr) return false;
        const [day, month, year] = dateStr.split("/").map(Number);
        const date = new Date(year, month - 1, day);
        return date.getFullYear() === year && date.getMonth() === month - 1 && date.getDate() === day;
    }

    function getCurrentDate() {
        const now = new Date();
        return now.toLocaleDateString("en-GB"); // dd/mm/yyyy
    }

    if (match) {
        const extractedDate = match[2];
        if (extractedDate && !isValidDate(extractedDate)) {
            return { error: "Invalid date format" };
        }
        return {
            text: match[1].trim(),
            date: extractedDate || getCurrentDate()
        };
    }

    return { error: "Input must contain 'gia vang/ty gia/tong hop'" };
}
return extractTextAndDate(mes);

